mapper.select:
SELECT email, passwd, phonenumber
FROM member
WHERE email = #{email}
AND  passwd = #{passwd}

mapper.update:
  UPDATE member
SET
    rememberme = #{rememberme, jdbcType=VARCHAR},
    rememberage = #{rememberage, jdbcType=DATE}
WHERE
    email = #{email}

UPDATE member 
SET
passwd = #{passwd},
phonenumber = #{phonenumber}
WHERE email = #{email}

mapper.delete:
DELETE FROM member
WHERE email = #{email}
  AND passwd = #{passwd}

mapper.select:
SELECT email
FROM member
WHERE phonenumber = #{phonenumber}

SELECT passwd
FROM member
WHERE email = #{email}
AND phonenumber = #{phonenumber}
